glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform FragConstants {
  vec2 atlas_size;
  vec2 texture_offset;
  vec2 mask_size;
  vec2 mask_offset;
};
uniform sampler2D mask;
void main () {
  vec4 mm = vec4(0.37647, 0.43922, 0.44706, 0);
  vec4 o = texture(texture, v_uv0);
  vec2 rp = v_uv0 * atlas_size + mask_size * 0.5 - texture_offset;
  vec4 m = texture(mask, (rp - mask_offset) / mask_size);
  if (m.a > 0.5 &&
      mask_offset.x <= rp.x && rp.x <= mask_size.x + mask_offset.x &&
      mask_offset.y <= rp.y && rp.y <= mask_size.y + mask_offset.y) {
    vec4 d = abs(o - mm);
    gl_FragColor = vec4(d.rgb, o.a);
  } else {
    gl_FragColor = o;
  }
}

glsl1.vert:
precision highp float;
uniform mat4 cc_matViewProj;
uniform mat4 cc_matWorld;
attribute vec3 a_position;
attribute vec4 a_color;
varying vec4 v_color;
attribute vec2 a_uv0;
varying vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl1.frag:
precision highp float;
#if USE_ALPHA_TEST
#endif
varying vec4 v_color;
varying vec2 v_uv0;
uniform sampler2D texture;
uniform vec2 atlas_size;
uniform vec2 texture_offset;
uniform vec2 mask_size;
uniform vec2 mask_offset;
uniform sampler2D mask;
void main () {
  vec4 mm = vec4(0.37647, 0.43922, 0.44706, 0);
  vec4 o = texture2D(texture, v_uv0);
  vec2 rp = v_uv0 * atlas_size + mask_size * 0.5 - texture_offset;
  vec4 m = texture2D(mask, (rp - mask_offset) / mask_size);
  if (m.a > 0.5 &&
      mask_offset.x <= rp.x && rp.x <= mask_size.x + mask_offset.x &&
      mask_offset.y <= rp.y && rp.y <= mask_size.y + mask_offset.y) {
    vec4 d = abs(o - mm);
    gl_FragColor = vec4(d.rgb, o.a);
  } else {
    gl_FragColor = o;
  }
}
