glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
out vec4 v_time;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  v_time = cc_time;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
in vec4 v_time;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform Constant {
  float flowSpeed;
  float flowWidth;
  float flowK;
  float flowCycle;
  float flowStrength;
  float lightSpeed;
  float opacity;
};
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  vec4 o2 = vec4(1, 1, 1, 1);
  float flowT = 1. / lightSpeed * flowSpeed;
  float t1 = mod(v_time.x * lightSpeed , 1.);
  float b1 = t1 * flowT - flowSpeed * 1.54;
  float b2 = b1 + flowWidth;
  #if USE_TEXTURE
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
  #endif
  o *= v_color;
  o2 *= o;
  o2.rgb = o2.rgb * o2.a + o2.rgb;
  float isValueBetween = float(clamp(t1, 0.1, 0.9) == t1);
  o2.a = (0.4 - abs(t1 - 0.5)) * opacity * isValueBetween;
  o.rgb += o2.rgb * o2.a;
  float isValueBetween2 = float(clamp(v_uv0.y, flowK * v_uv0.x + b1, flowK * v_uv0.x + b2) == v_uv0.y);
  o.rgb = mix(o.rgb, o.rgb * flowStrength, isValueBetween2);
  #if USE_BGRA
    gl_FragColor = o.bgra;
  #else
    gl_FragColor = o.rgba;
  #endif
}

