glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
out vec4 v_time;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  v_time = cc_time;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
in vec4 v_time;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
#if USE_MASK_TEXTURE
uniform sampler2D maskTexture;
#endif
uniform sampler2D flowTexture;
uniform Constant {
  float speed;
};
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  vec4 maskTextureColor = vec4(1,1,1,1);
  vec4 flowTextureColor = vec4(1,1,1,1);
  vec2 dir = normalize(vec2(1., 1.) - vec2(0.5, 0.5));
  float angle = fract(v_time.x * speed) * 6.28;
  float dist = 0.5;
  float ratio = 1.0;
  dir = vec2(dir.x * cos(angle) - dir.y * sin(angle), dir.x * sin(angle) + dir.y * cos(angle));
  vec2 uvFromCenter = v_uv0 - vec2(0.5, 0.5);
  #if IS_REVERSE
    dist = 0.;
    ratio = 2.0;
  #endif
  float dirColor = abs(dot(uvFromCenter, dir) + dist) * ratio;
  vec4 flowTexture_tmp = texture(flowTexture, vec2(dirColor, 0.));
  #if CC_USE_ALPHA_ATLAS_flowTexture
      flowTexture_tmp.a *= texture(flowTexture, vec2(dirColor, 0.) + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    flowTextureColor.rgb *= (flowTexture_tmp.rgb * flowTexture_tmp.rgb);
    flowTextureColor.a *= flowTexture_tmp.a;
  #else
    flowTextureColor *= flowTexture_tmp;
  #endif
  float visible = 1.;
  #if USE_TEXTURE
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
  #endif
  #if USE_MASK_TEXTURE
  vec4 maskTexture_tmp = texture(maskTexture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_maskTexture
      maskTexture_tmp.a *= texture(maskTexture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    maskTextureColor.rgb *= (maskTexture_tmp.rgb * maskTexture_tmp.rgb);
    maskTextureColor.a *= maskTexture_tmp.a;
  #else
    maskTextureColor *= maskTexture_tmp;
  #endif
    visible = step(0.1, maskTextureColor.a);
  #endif
  float isBright = step(0.5, o.r * 0.2126 + 0.7152* o.g + 0.0722 * o.b);
  o *= v_color;
  o.rgb = mix(o.rgb, flowTextureColor.rgb, visible * isBright);
  #if USE_BGRA
    gl_FragColor = o.bgra;
  #else
    gl_FragColor = o.rgba;
  #endif
}

